import React, { useState, useEffect, useRef } from 'react';
import './CircleFollow.css'; // Import CSS file for styling

function CircleFollow() {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 }); // Initial mouse position
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    function handleMouseMove(e) {
      // Update mouse position
      setMousePosition({ x: e.pageX, y: e.pageY });

      // Clear previous drawings
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw a black circle at mouse position
      ctx.fillStyle = 'black';
      ctx.beginPath();
      ctx.arc(mousePosition.x, mousePosition.y, 10, 0, Math.PI * 2);
      ctx.fill();

      // Invert the colors within the circle
      ctx.globalCompositeOperation = 'difference';
      ctx.fillStyle = 'white';
      ctx.globalAlpha = 0.5;
      ctx.beginPath();
      ctx.arc(mousePosition.x, mousePosition.y, 10, 0, Math.PI * 2);
      ctx.fill();

      // Reset global properties
      ctx.globalCompositeOperation = 'source-over';
      ctx.globalAlpha = 1;
    }

    document.addEventListener('mousemove', handleMouseMove);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, [mousePosition]);

  return <canvas ref={canvasRef} className="circle-follow-canvas" />;
}

export default CircleFollow;
