import React, { useState, useEffect } from 'react';
import './CircleFollow.css'; // Import CSS file for styling

function MouseCircle() {
  const [circleColor, setCircleColor] = useState('black'); // Initial color

  useEffect(() => {
    function handleMouseMove(e) {
      // Calculate the opposite color of the background
      const backgroundColor = getComputedStyle(document.body).backgroundColor;
      const oppositeColor = getOppositeColor(backgroundColor);

      // Update circle color
      setCircleColor(oppositeColor);

      // Position the circle around the mouse cursor
      const circle = document.getElementById('mouse-circle');
      circle.style.left = e.pageX + 'px';
      circle.style.top = e.pageY + 'px';
    }

    document.addEventListener('mousemove', handleMouseMove);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  function getOppositeColor(color) {
    // Convert color to RGB
    const match = color.match(/^rgb\((\d+),\s*(\d+),\s*(\d+)\)$/);
    if (!match) return;

    // Calculate opposite color
    const oppositeColor = `rgb(${255 - match[1]}, ${255 - match[2]}, ${255 - match[3]})`;
    return oppositeColor;
  }

  return (
    <div id="mouse-circle" style={{ backgroundColor: circleColor }}></div>
  );
}

export default MouseCircle;
