import React, { useState, useEffect } from 'react';
import './CustomCursor.css'; // Import CSS file for styling

function CustomCursor() {
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 }); // Initial cursor position
  const [cursorColor, setCursorColor] = useState('black'); // Initial cursor color
  const [cursorSize, setCursorSize] = useState(20); // Initial cursor size

  useEffect(() => {
    function handleMouseMove(e) {
      // Update cursor position
      setCursorPosition({ x: e.clientX, y: e.clientY });

      // Get the element under the cursor
      const element = document.elementFromPoint(e.clientX, e.clientY);

      // Get the background color of the element
      const backgroundColor = window.getComputedStyle(element).getPropertyValue('background-color');

      // Calculate the opposite color for good contrast
      const oppositeColor = calculateOppositeColor(backgroundColor);
      setCursorColor(oppositeColor);
    }

    document.addEventListener('mousemove', handleMouseMove);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  function calculateOppositeColor(color) {
    // Convert color to RGB
    const match = color.match(/^rgb\((\d+),\s*(\d+),\s*(\d+)\)$/);
    if (!match) return 'white'; // Default to white if unable to parse color

    // Calculate opposite color (inverting each RGB value)
    const oppositeColor = `rgb(${255 - match[1]}, ${255 - match[2]}, ${255 - match[3]})`;
    return oppositeColor;
  }

  return (
    <div className="custom-cursor" style={{ left: cursorPosition.x, top: cursorPosition.y, backgroundColor: cursorColor, width: cursorSize, height: cursorSize }}></div>
  );
}

export default CustomCursor;
